INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (40,'V70','SS 33. Lavori di collegamento tra la S.S. 33 e la S.S. 34 di raccordo al casello della A26 in variante all''abitato di Gravellona Toce',2,11,6,NULL,NULL,'ANAS S.p.A.','L''intervento si articola in un tratto in nuova sede dello sviluppo di circa 1400 m e in un tratto in ammodernamento in sede dello sviluppo di circa 820 m. TRATTO IN NUOVA SEDE L''attestamento del nuovo tracciato alla S.S. 33 "del Sempione" è previsto mediante svincolo di tipologia a rotatoria a raso avente raggio in asse pari a 25.00 m, opportunamente raccordata alla viabilità esistente; tale rotatoria occupa un''area compresa tra la strada statale anzidetta e il torrente Stronetta, si sviluppa alla quota dell''attuale sedime viario e pertanto in rilevato rispetto al piano campagna di altezza pari a circa 3,70 m. Da un punto di vista planimetrico si prevede una curva in sinistra cui segue un rettilineo; altimetricamente il tracciato si diparte con una livelletta orizzontale per sovrappassare il torrente Stronetta con un ponte di luce 30.00 m, per portarsi al piano campagna (quota progetto: 199.00 m). Alla progressiva 0+450 il tracciato interseca la Via Nuova dove risulta prevista una rotatoria a raso avente raggio in asse pari a 25.00 m; tale rotatoria svolge funzione di collegamento locale all''area industriale esistente e riveste particolare importanza per la funzionalità dell''opera intesa come variante all''abitato di Gravellona Toce. Il tracciato prosegue mediante andamento planimetrico secondo una curva in sinistra  cui segue un rettilineo; altimetricamente il tracciato si sviluppa con una livelletta orizzontale in corrispondenza della predetta rotatoria, per portarsi in rilevato alla quota necessaria per lo scavalcamento della Via Della Chimica ove ha inizio il viadotto. Il tracciato ha termine in corrispondenza della S.S. 34 "dell''Alto Verbano"  con una rotatoria di raggio in asse pari a 25.00 m, opportunamente raccordata alla viabilità esistente; tale rotatoria si sviluppa verso nord rispetto alla strada esistente e  consente l''iinserimento della viabilità esistente di connessione a Mergozzo attualmente svincolata mediante incrocio a "T". TRATTO IN SEDE L''intervento ha inizio in corrispondenza della Via Stronetta, dove risulta prevista la rotatoria a raso di cui all''intervento della Variante di Omegna; lungo tale tratto è previsto l''adeguamento della sezione trasversale mediante inserimento della banchine laterali da 1.50 m e ove necessario corsie di accelerazione e decelerazione da 3.50 m per la canalizzazione degli accessi esistenti con eliminazione delle svolte a sinistra mediante opportune cordolature controstrade. Risultano pertanto oggetto di tali sistemazioni n. 7 accessi lungo la direzione Gravellona- Baveno e n. 5 accessi lungo la direzione Baveno- Gravellona. Inoltre alla rotatoria di inizio intervento del tratto in nuova sede si prevede l''interconnessione della viabilità esistente a completamento degli accessi degli insediamenti industriali esistenti.','Il proponente ha lasciato scadere i termini della proroga per la trasmissione delle integrazioni richieste in sede di CdS VIA. Ha proceduto al ritiro del progetto',NULL,NULL,NULL,TIMESTAMP'2012-02-08 00:00:00.0',TIMESTAMP'2017-09-08 11:10:17.0','10',NULL,40,1);
