INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (120,'F27','Grandi Stazioni - Infrastrutture complementari alla stazione ferroviaria di Torino Porta Nuova',1,7,10,NULL,NULL,'Grandi Stazioni SpA','Gli interventi denominati "infrastrutture complementari alla stazione di Torino Porta Nuova" comprendono la costruzione di parcheggi di varie tipologie (a raso, interrati, in edifici multipiano), la realizzazione di infrastrutture da destinare a servizi, la sistemazione di aree esterne, soprattutto in relazione all''interscambio con altri sistemi di trasporto pubblico e privato, e la creazione di sistemi di videosorveglianza finalizzati a riqualificare i complessi di stazione sotto l''aspetto della sicurezza.','Progetto Preliminare approvato con  delibera CIPE 10/2003. Delibera CIPE 44/2004 relativa  all''approvazione del progetto definitivo del "sistema integrato di videosorveglianza", scorporato dal progetto delle infrastrutture complementari. Delibera CIPE 129/2006 relativo all''approvazione del progetto definitivo delle "opere complementari".','Il 10/02/2010 la Direzione Trasporti ha formulato le osservazioni sul recepimento delle prescrizioni del Cipe nel Progetto Esecutivo. La documentazione relativa alle suddette verifiche di ottemperanza è stata trasmessa da parte della Soc. Grandi Stazioni in data 31/05/2010. I lavori sono stati avviati a fine anno 2010; a causa di una serie di difficoltà finanziarie dell''Impresa mandataria dell''A.T.I., aggiudicataria, hanno subito forti rallentamenti. In data 15 giugno u.s. si è concluso un processo di acquisizione delle quote della mandataria dell''ATI da parte di una delle mandanti (Guerrato s.p.a.) consentendo in tal modo la continuità dell''appalto. Sulla base degli elementi sopra descritti era stata ipotizzata una protrazione dei tempi d''esecuzione di circa 6 mesi rispetto alla prevista ultimazione fissata al 23 luglio 2013. Al momento i lavori risultano in corso ma non si dispone di ulteriori informazioni. Per quanto attiene l''impianto di VIDEOSORVEGLIANZA INTEGRATA trattasi di affidamento autonomo per l''esecuzione dell''intervento in tutte e 13 le stazioni.',NULL,'Grandi Stazioni SpA',TIMESTAMP'2012-02-08 00:00:00.0',TIMESTAMP'2018-04-11 16:27:03.0','134',NULL,120,1);
