INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (130,'F34','Nodo di Torino. Attivazione fermata Dora sul Passante',2,7,1,NULL,NULL,'R.F.I. SpA','L''intervento riguarda la progettazione e realizzazione delle opere civili e impiantistiche per l''attivazione della fermata Dora, sita nel Passante Ferroviario di Torino tra le stazioni di Porta Susa e Rebaudengo Fossata, con l''utilizzo dei due binari della linea "Passante" (ovest). Secondo il progetto strutturale approvato dal Comitato di Coordinamento del Nodo di Torino nel luglio 2003, le banchine, con modulo 250 m e larghezza tra 4,9 e 5,85 m, sono previste a servizio dei due binari della "Linea Passante" (ovest). In corrispondenza della Linea "Storica"(est) non è previsto servizio di fermata per i viaggiatori. L''assetto prevede infatti solo gli elementi necessari all''esodo dei viaggiatori in caso di emergenza ed accesso dei soccorsi in galleria. 
INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (132,'F37','Nodo di Torino. Attivazione fermata Zappata sul Passante',2,7,1,NULL,NULL,'R.F.I. S.p.A.','progettazione e realizzazione degli impianti e delle finiture architettoniche necessarie all''attrezzaggio della fermata Zappata (le opere civili al rustico sono state concluse da RFI SpA)','Completato (disponibile solo su archivio cartaceo e non su file) lo studio di fattibilità  RFI risalente ai primi anni ''80. Sono necessari ora: Digitalizzazione elaborati cartacei e rilievi: 90 gg. Affidamento incarico progettazione (comprensivo della predisposizione della documentazione di gara): 180 gg. Progetto preliminare: 120 gg. Approvazione progetto preliminare a cura di RFI: 30 gg. Progetto definitivo: 210 gg. Approvazione progetto definitivo a cura di RFI: 30 gg. Finchè non ci sono tempi certi sulla disponibilità del finanziamento, non è possibile inserire e aggiornare le date mancanti','Sono necessari: - Predisposizione bando e espletamento gara con aggiudicazione: 180 gg; - Progettazione esecutiva: 150 gg; - Approvazione progetto esecutivo: 30 gg; - Esecuzione lavori: 540 gg; - Attivazione: 1 gg; - Collaudo: 90 gg. Finchè non ci sono tempi certi sulla disponibilità del finanziamento, non è possibile inserire e aggiornare le date mancanti',NULL,NULL,TIMESTAMP'2012-02-08 00:00:00.0',TIMESTAMP'2018-12-20 12:21:13.0','29',NULL,132,1);
