INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (156,'V01.03a','Autostrada Asti - Cuneo. Lotti 2.1b - 2.1dir - 2.5 - 1.6 ',1,11,10,NULL,NULL,'Asti-Cuneo Spa (in corso revisione)','I lotti realizzati e da realizzarsi in concessione hanno una lunghezza complessiva pari a circa 50 km e permetteranno il completamento dell''autostrada Asti-Cuneo; come per i lotti della stessa Asti-Cuneo realizzati da ANAS SpA e già conclusi è prevista la costruzione di una sede stradale con due corsie più emergenza per ogni senso di marcia.Nel dettaglio: del I° tronco (Massimini-Cuneo) sono stati realizzati dalla Società Asti-Cuneo SpA i lotti I-3/4  Consovero -Castelletto Stura-Cuneo della lunghezza di 10,213 km ed I-5 Cuneo - SS 231della lunghezza di  3,808 Km, mentre restano da realizzarsi il Lotto I-6  o Tangenziale di Cuneo, 1° lotto funzionale, della lunghezza di  7,55 km, del II tronco (Asti-Marene) è stato completato il Lotto II-1a Isola d''Asti-Rocca Schiavino Lunghezza Km 2,230 mediante allargamento di sede stradale esistente, mentre il  Lotto II-1b  (Rocca Schiavino-Asti Est  Km 6,650), il Lotto II-1dir  (Rocca Schiavino-Asti Ovest  Km 5,20), Lotto II-5 (Guarene-Roddi  Km 6,158), Lotto II-6 (Roddi-Diga Enel  Km 8,888), sono ancora da realizzarsi. ','I progetti definitivi dei lotti realizzati e da realizzarsi dalla concessionaria sono stati autorizzati in diverse Conferenze di Servizi del Ministero delle Infrastrutture e dei Trasporti (allora Ministero dei Lavori Pubblici), tenutesi tra il 1999 e il 2003 e secondo la procedura prevista dall''art.81 del DPR 616/1977 e s.m.e i.  I lotti II.1dir e I.6 sono stati aggiunti successivamente all''approvazione (avvenuta negli anni 1999-2002) dei lotti costituenti il tracciato originario ed hanno seguito pertanto la procedura del D.lgs 163/2006 (come già disciplinato dal  D.lgs 190/2002) in quanto l''infrastruttura è stata inserita tra le opere strategiche così come definite dalla L. 443/2001. Successivamente con provvedimento del 05/11/2008 del Ministero delle Infr. è stato  rinnovato il vincolo preordinato all''esproprio di tutto il lotto II.1 in questo provvedimento è stata compresa la Valutazione d''Incidenza sul SIC degli Stragni di Belangero. Anche sui lotti I-3,4 ed I.5 il Ministro delle Infr., con provvedimento del 24/01/2008, ha rinnovato il vincolo preordinato all''esproprio e l''autorizzazione paesaggistica, sul Lotto II-5 e II-6 in sede di rinnovo del vincolo  è stata dal MATTM richiesta la Valutazione I.A.. Sul progetto preliminare del Lotto II-1dir (tangenziale sud-ovest) in procedura di VIA sono state richieste al proponente delle elaborazioni progettuali per sciogliere le problematiche dell''impatto paesaggistico riscontate dal Ministero per i Beni Culturali (MiBAC), al momento tale documentazione non è pervenuta. Il Lotto  I-6 ( o 1° lotto della Tangenziale di Cuneo) ha avuto un''approvazione del preliminare con Delibera CIPE n. 022  il 21.02.2008; con DGR del settembre 2010 la Regione Piemonte ha espresso il proprio parere sul progetto definitivo, mentre non è avvenuta l''approvazione da parte del CIPE. Per il Lotto II-6  con DGR 5-1992 del 9.05.2011 RP ha espresso il proprio parere di VIA al ministero dell''Ambiente che a sua volta ha chiuso il procedimento con il Decreto VIA 576 del 2011. Successivamente per la localizzazione delle opere il Ministero delle Infrastrutture  si è espresso con provvedimento 6916 del 06.08.2012. Si è in seguito avviata la verifica di ottemperanza delle prescrizioni dopo che  è stata chiusa dal Ministero dell''Ambiente la procedura di Verifica di Assogettabilità alla VIA di alcune modifiche progettuali apportate al progetto autorizzato. In tale procedura Regione Piemonte ha espresso con determinazione n. 151 del 26.06.2014 le proprie osservazioni. E'' stata altresì conclusa positivamente la procedura per l''approvazione del Piano di Utilizzo delle terre e rocce da scavo con Determinazione del MATTM n. 1564 del 19/01/2015. Su questo lotto 2.6 è in corso la revisione progettuale ai sensi dell''art.200 del D.lgs 50/2016 e s.m.i., finalizzata al contenimento dei costi.','La realizzazione dei lotti mancanti era stata affidata nel 2005 alla società Asti-Cuneo SpA unitamente alla gestione dell'' intera infrastruttura autostradale, con Convenzione di Concessione stipulata in data 01/08/2007 e approvata con decreto interministeriale n. 25691 del 22/11/2007 e registrata alla Corte dei Conti  Reg. n. 1 foglio 76 in data 31/01/2008.  Tale concessione aveva previsto una durata di quattro anni dei lavori dalla notifica per la realizzazione delle opere. Sono stati iniziati a dicembre 2008 e terminati a febbraio 2012, i lavori dei lotti I-3, I-4 e I-5, che ricadono tutti nel territorio comunale di Cuneo e il cui tracciato ha una lunghezza complessiva di 14 km, mentre i lavori del lotto II-1a, (Comune di Isola d'' Asti) sono iniziati a fine 2009 e terminati nel giugno 2012.',NULL,NULL,TIMESTAMP'2012-02-08 00:00:00.0',TIMESTAMP'2019-01-28 10:40:10.0','28',NULL,156,1);
INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (164,'V10','Collegamento stradale Masserano - Ghemme',1,11,15,'C21B08000240001',NULL,'ANAS S.p.A.','Il progetto dell''infrastruttura Masserano-Ghemme è parte del più ampio progetto denominato "Pedemontana Piemontese" inizialmente previsto quale collegamento autostradale finalizzato a collegare la Città di Biella con le autostrade A26 ed A4. Quale soggetto concedente ed aggiudicatore dell''intervento era stata  individuata la Soc.CAP SpA (ANAS e Regione Piemonte).Le  dinamiche economiche attuali con la relativa riduzione del traffico veicolare inizialmente previsto, ed il conseguente mancato equilibrio del Piano Finanziario del promotore, hanno determinato l''esigenza di una revisione della progettazione pervenendo ad individuare la tratta Masserano Ghemme di 13,7 km in sezione B quale 1a fase di attuazione della Pedemontana Piemontese. Gli svincoli di Masserano, Gattinara, Ghemme a cui si aggiunge quello di Roasio, sono quelli previsti, con lo svincolo di Ghemme che consentirà la interconnessione alla A26. ','Pregresso: Il 30/03/2010 è stato attivato il procedimento di approvazione del progetto preliminare dell''opera dell''intera autostrada Pedemontana presentato dal promotore, secondo le procedure di Legge Obiettivo, con valutazione d''impatto ambientale statale e formazione dell''Intesa Stato-Regione in merito alla localizzazione.
INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (178,'V54.01','Completamento e ottimizzazione della Torino-Milano. Interconnessione tra la SS 32 e la SP 299-tangenziale di Novara lotto 0 e I',2,11,7,NULL,NULL,'ANAS S.p.A.','Il tracciato in progetto si connette al tronco esistente della Tangenziale di Novara che attualmentesvincola sulla S.S. 32 "Ticinese" in corrispondenza della casa cantoniera dell''Anas ubicata a Nord dell''abitato di Cameri. La curvatura planimetrica iniziale prosegue l''andamento esistente. Successivamente l''asse prosegue in direzione est-ovest attraversando il Torrente Terdoppio con un nuovo viadotto a 9 campate. Supera quindi la linea ferroviaria Alessandria - Arona (ponte a campata singola) e la linea ferroviaria Novara - Domodossola con l''adiacente S.S. 229 (viadotto a 4 campate); proprio sulla S.S. 229 è  inserito il primo svincolo che raccorda il lotto di cui trattasi con una rotatoria a raso ubicata a cavallo della statale. L''asse in progetto prosegue poi verso il suo punto d''arrivo con una inflessione planimetrica verso sud-ovest mantenendosi in rilevato; scavalca infine la linea ferroviaria Vignale - Varallo, la Roggia Mora, il torrente Agogna e la S.S. 299 ''della Valsesia''  con un viadotto complessivo di 13 campate. Il raccordo alla citata statale 299 avviene con una rampa che si innesta sull''esistente rotatoria recentemente realizzata a cura della Provincia di Novara.',NULL,NULL,NULL,NULL,TIMESTAMP'2012-02-08 00:00:00.0',TIMESTAMP'2019-12-04 09:46:50.0','13',NULL,178,1);
INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (179,'V54.02','Completamento e ottimizzazione della Torino-Milano. Tangenziale di Novara V lotto (L. 350/2003)',1,11,2,NULL,NULL,'ANAS/SATAP','L''intervento consiste nella realizzazione dei primi due lotti dell''intero anello previsto intorno a Novara situati nella zona Nord e completamento con il lotto 5 posto ad Ovest. Il lotto 5, unitamente ai lotti 0 e 1 (vedi scheda V54.01), costituisce il completamento della tangenziale di Novara, nel suo complesso funzionale anche alla ricollocazione del terminal Huckpepack ed al potenziamento del Centro Intermodale  di Novara (CIM). (Punto 6 del Protocollo di Intesa Quadro sull''Assetto Ferroviario del Nodo di Novara del 28/07/2004).','Il PdI prevede che l''approvazione dell''intervento avvenga secondo le procedure proprie della Provincia. Per il lotto V esiste uno Studio di Fattibilità da parte della Provincia di Novara inoltrato al Ministero dei Trasporti.',NULL,NULL,NULL,TIMESTAMP'2012-02-08 00:00:00.0',TIMESTAMP'2018-01-22 09:27:55.0','28',NULL,179,1);
