INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (202,'V48.23','SR 20 Adeguamento funzionale tratto fra circonvallazione di La Loggia e circonvallazione di Carignano (per ca. 1,5 Km)',2,11,8,NULL,NULL,'S.C.R. Piemonte S.p.A.','L''intervento ha come oggetto l''adeguamento funzionale della ex S.R. 20 nel tratto fra la circonvallazione di La Loggia e la circonvallazione di Carignano, per uno sviluppo complessivo di circa 2,2 km. L''intervento presenta le seguenti caratteristiche peculiari: - Adeguamento della sezione esistente alle caratteristiche tecniche della sezione stradale tipo C1 del D.M. 05/11/2001; - Eliminazione degli accessi diretti alla ex S.R. 20 mediante raggruppamento degli stessi tramite strade di servizio complanari di sezione adeguata al volume di traffico; - Realizzazione di una rotatoria in corrispondenza all''incrocio con la Strada Vicinale "dei Brassi" e "degli Olmi"; - Congiungimento delle strade laterali di servizio con la rotatoria in progetto o con le strade vicinali; - Adeguamento delle piazzole di sosta e della segnaletica alla normativa vigente; - Realizzazione di canali di raccolta per le acque di prima pioggia e gli sversamenti accidentali; - Realizzazione di un sistema di depurazione (disoleazione) delle acque di prima pioggia realizzato mediante soglie e disoleatori posti all''interno dei fossati stessi.','Intervento sospeso con D.G.R. n. 123-1877 del 20.07.2015.',NULL,'S.C.R. Piemonte S.p.A.',NULL,TIMESTAMP'2012-02-08 00:00:00.0',TIMESTAMP'2017-12-20 14:42:39.0','17',NULL,202,1);
INSERT INTO moti_T_INTERVENTO (ID_INTERVENTO,COD_INTERVENTO,TITOLO,FK_SETTORE,FK_TIPOLOGIA,FK_STATO_ATTUAZIONE,CUP,CIG,ATTUATORE,DESCR_TECNICA,NOTE_PROGRAMMAZIONE,NOTE_REALIZZAZIONE,FONTE_DATI_PROGRAMMAZIONE,FONTE_DATI_REALIZZAZIONE,DATA_INSERIMENTO,DATA_ULTIMA_MODIFICA,FK_UTENTE_ULTIMA_MODIFICA,DATA_CANCELLAZIONE,ID_INTERVENTO_OLD,FK_FONTE_DATI) VALUES (203,'V48.24','SR 20 Adeguamento funzionale tratto fra SP 142, abitato di Carmagnola e ponte sul Po',2,11,6,'F44E02000020002',NULL,'S.C.R. Piemonte S.p.A.','La lunghezza totale della strada risulta pari a circa 2100 m composta dall''adeguamento del ponte "Carmagnola" sul fiume Po di lunghezza pari a 122 m, la restante parte in sede naturale (in gran parte rilevati). In sintesi, i criteri base del progetto sono: ·nel tratto iniziale, dall''attuale rotatoria fino alla progressiva 1+000 circa, si è realizzato l''allargamento della sede stradale in sinistra, al fine di mantenere inalterato l''argine fluviale esistente; -in corrispondenza del ponte sul fiume Po, l''allargamento avviene in asse; ·dalla progressiva 1+350 alla progressiva 1+750 circa, l''allargamento della sede stradale torna in sinistra al fine di mantenere inalterato il rilevato stradale in vicinanza del fiume; ·dalla progressiva 1+750 fino alla fine dell''intervento, l''allargamento torna in asse, concludendosi nella nuova rotatoria progettata. Lungo il tracciato sono state previste piazzole di sosta così disposte: ·in sinistra alle progressive 0+250, 0+800, 1+610; ·in destra alle progressive 0+100, 1+080, 1+460.','Intervento riavviato con la rimodulazione di cui alla D.G.R. 123-1877 del 20.07.2015. E'' in fase di definizione il progetto definitivo da parte del raggruppamento a cui è stato aggiudicato il servizio con Disposizione del CD n. 10 del 17/01/2019 ',NULL,'S.C.R. Piemonte S.p.A.',NULL,TIMESTAMP'2012-02-08 00:00:00.0',TIMESTAMP'2020-03-02 15:57:22.0','13',NULL,203,1);
